import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Navv from "./Navbar";
import Footer from "./Footer";

function ProductBy() {
  const { id } = useParams();  // Get the product ID from the route params
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:4500/products/${id}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        setError("Failed to retrieve product details");
      });
  }, [id]);

  if (error) {
    return <p className="text-red-500 text-center mt-4">{error}</p>;
  }

  return (
    <>
      <Navv />
      {product ? (
        <div className="container mx-auto p-4">
          <div className="flex flex-col lg:flex-row">
            <img
              alt={product.title}
              src={product.image}
              className="h-full w-full object-cover object-center lg:h-96 lg:w-1/2"
            />
            <div className="lg:ml-4 mt-4 lg:mt-0">
              <h1 className="text-3xl font-bold text-gray-900">{product.title}</h1>
              <p className="text-xl font-medium text-gray-900">${product.price}</p>
              <p className="mt-4 text-gray-600">{product.description}</p>
              <p className="mt-4 text-gray-500">Rating: {product.rating?.rate || "No rating"} ({product.rating?.count || "No reviews"})</p>
              <div className="mt-4 flex space-x-2">
                <button type="button" className="text-white bg-gray-800 hover:bg-gray-900 focus:outline-none focus:ring-4 focus:ring-gray-300 font-medium rounded-full text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:hover:bg-gray-700 dark:focus:ring-gray-700 dark:border-gray-700">Buy</button>
                <button type="button" className="text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-100 font-medium rounded-full text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-700">Add to Cart</button>              
              </div>
            </div>
          </div>
        </div>
      ) : (
        <p className="text-gray-500 text-center mt-4">Loading...</p>
      )}
      <Footer />
    </>
  );
}

export default ProductDetail;
