import React, { useState, useEffect } from "react";
import axios from "axios";
import { loadStripe } from "@stripe/stripe-js";
import { useNavigate } from "react-router-dom";

// Load Stripe with your publishable key
console.log("Stripe Public Key:", );
const stripePublicKey = "pk_test_51PvxtOEouuRn1o70nzGkxIGTL2kJgmOkY97fqhsLlLlYiVkS7ZrZUlDmXgEDa7Uj8ZXnBtFPxPIOVfvBlUGKBPmF0041TGGBXD";

const stripePromise = loadStripe(stripePublicKey);

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch cart items from local storage or server
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  const handleCheckout = async () => {
    const stripe = await stripePromise;

    try {
      // Send the cart data to the server to create a checkout session
      const { data } = await axios.post("http://localhost:4500/create-checkout-session", {
        line_items: cart.map(item => ({
          price_data: {
            currency: 'usd',
            product_data: {
              name: item.title,
            },
            unit_amount: item.price * 100,
          },
          quantity: item.quantity,
        })),
      });

      // Redirect to Stripe Checkout
      const result = await stripe.redirectToCheckout({
        sessionId: data.id,
      });

      if (result.error) {
        console.error(result.error.message);
        setError("An error occurred during checkout. Please try again.");
      }
    } catch (error) {
      console.error("Error during checkout:", error);
      setError("Checkout process failed. Please try again.");
    }
  };

  return (
    <div className="p-4 max-w-lg mx-auto">
      <h1 className="text-2xl font-bold mb-4">Checkout</h1>
      <div>
        <h2 className="text-xl font-semibold mb-2">Your Cart</h2>
        <ul>
          {cart.map((item) => (
            <li key={item.id} className="flex justify-between mb-2">
              <span>{item.title}</span>
              <span>${item.price} x {item.quantity}</span>
            </li>
          ))}
        </ul>
        <button
          type="button"
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={handleCheckout}
        >
          Proceed to Payment
        </button>
        {error && <p className="text-red-500 mt-4">{error}</p>}
      </div>
    </div>
  );
};

export default Checkout;
