import React from "react";
import Navv from "./Navbar";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Main = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    var settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
      };
    
    useEffect(() => {
      axios.get("http://localhost:4500/products")  // Adjust the URL to match your backend
        .then((response) => {
          setProducts(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError("Failed to retrieve products");
          setLoading(false);
        });
    }, []);

    if (loading) return <p>Loading products...</p>;
    if (error) return <p>{error}</p>;
  
return(
<>
<Navv/>
<h2>welcome to ecommerce store</h2>
<Slider {...settings}>
      {products.map((product) => (
        <div key={product._id} style={{ padding: "0 10px" }}>
          <div style={{ border: "1px solid #ddd", padding: "20px", textAlign: "center" }}>
            <img src={product.image} alt={product.name} style={{ width: "55%", height: "auto" }} />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
          </div>
        </div>
      ))}
    </Slider>
</>
);
}

export default Main;
